var rucksacks = input.Split("\r\n");

var counter = 0;
var counter2 = 0;
//Part 1
foreach (var rucksack in rucksacks)
{
    var endOfFirstCompartment = rucksack.Length / 2;

    var compartment1 = rucksack.Substring(0, endOfFirstCompartment).ToCharArray();
    var compartment2 = rucksack.Substring(endOfFirstCompartment, rucksack.Length - endOfFirstCompartment).ToCharArray().ToList();

    foreach (var item in compartment1)
    {
        if (compartment2.Contains(item))
        {
            compartment2.RemoveAll(i => i == item);

            counter += ValueOfItem(item);
        }
    }
}

int ValueOfItem(char item)
{
    if ((int)item >= 97)
        return item - 96;
    else
        return item - 38;
}

