for (int i = 0; i < rucksacks.Length; i = i + 3)
{
    var rucksack1 = rucksacks[i].ToCharArray().ToList();
    var rucksack2 = rucksacks[i+1].ToCharArray();
    var rucksack3 = rucksacks[i+2].ToCharArray();

    foreach (var item in rucksack1.ToList())
    {
        if(rucksack2.Contains(item) && rucksack3.Contains(item))
        {
           rucksack1.RemoveAll(i => i == item);
           counter2 += ValueOfItem(item);

           break;
        }
    }
}

