
var input = "";

Find(4);
Find(14);

void Find(int countTo)
{
    var packet = new List<char>();
    var counter = 0;
    foreach (var c in input.ToCharArray())
    {
        counter++;

        if (!packet.Contains(c))
            packet.Add(c);
        else
        {
            packet.RemoveRange(0, packet.IndexOf(c) + 1);
            packet.Add(c);
        }

        if (packet.Count == countTo)
            break;
    }

    Console.WriteLine(counter);
}

