var inputArray = input.Split(Environment.NewLine)
                       .Select(s => s.Split(" -> ")
                       .Select(ss => 
                            (x: int.Parse(ss.Split(',')[0]),
                             y: int.Parse(ss.Split(',')[1])
                        )).ToList());

var x = inputArray.SelectMany(ii => ii.Select(a => a.x));
var y = inputArray.SelectMany(ii => ii.Select(a => a.y));

var emptyCave = new string[y.Max() + 1, x.Max() - 448];
var emptyCave2 = new string[y.Max() + 3, x.Max()];

//Draw Stone
foreach (var item in inputArray)
{
    for (int i = 0; i < item.Count; i++)
    {
        emptyCave[item[i].y, item[i].x - 450] = "#";
        emptyCave2[item[i].y, item[i].x - 250] = "#";

        if (i + 1 < item.Count)
        {
            var minX = Math.Min(item[i].x, item[i + 1].x);
            var maxX = Math.Max(item[i].x, item[i + 1].x);
            var minY = Math.Min(item[i].y, item[i + 1].y);
            var maxY = Math.Max(item[i].y, item[i + 1].y);
            for (int v = minY; v <= maxY; v++)
            {
                for (int h = minX - 450; h <= maxX - 450; h++)
                {
                    emptyCave[v, h] = "#";
                }

                for (int h = minX - 250; h <= maxX - 250; h++)
                {
                    emptyCave2[v, h] = "#";
                }
            }
        }
    }
}

//Draw floor for part 2
for (int i = 0; i < emptyCave2.GetLength(1); i++) {
    emptyCave2[emptyCave2.GetLength(0) - 1, i] = "#";
}


#region Part 1
var canAddSand = true;
var yS = 1;
var xS = 50;
while (canAddSand)
{
    CheckCoords(yS, xS, false, emptyCave);
}
var sandCounter = 0;

//Draw Cave
for (int i = 0; i < emptyCave.GetLength(0); i++)
{
    for (int j = 0; j < emptyCave.GetLength(1); j++)
    {
        if (i == 0 && j == 50)
            Console.Write("+");

        if (emptyCave[i, j] == "#")
            Console.Write(emptyCave[i, j]);
        else if (emptyCave[i, j] == "o")
        {
            sandCounter++;
            Console.Write(emptyCave[i, j]);
        }
        else
            Console.Write(".");
    }

    Console.WriteLine();
}

Console.WriteLine(sandCounter);
#endregion

#region Part 2
//Build sand
yS = 1;
xS = 250;
canAddSand = true;
while (canAddSand)
{
    CheckCoords(yS, xS, true, emptyCave2);
}

//Draw Cave2
sandCounter = 0;
for (int i = 0; i < emptyCave2.GetLength(0); i++)
{
    for (int j = 0; j < emptyCave2.GetLength(1); j++)
    {
        if (emptyCave2[i, j] == "#")
            Console.Write(emptyCave2[i, j]);
        else if (emptyCave2[i, j] == "o")
        {
            sandCounter++;
            Console.Write(emptyCave2[i, j]);
        }
        else
            Console.Write(".");
    }

    Console.WriteLine();
}

Console.WriteLine(sandCounter);

#endregion

void DrawAndReset(int yc, int xc, string[,] cave, bool isPart2)
{
    cave[yc, xc] = "o";
    yS = 1;
    xS = isPart2 ? 250 : 50;
    return;
}

void CheckCoords(int yc, int xc, bool isPart2, string[,] cave)
{
    if (!isPart2 && yc == y.Max() + 1)
    {
        canAddSand = false;
        return;
    }

    if (cave[yc, xc] == null)
    {
        yS++;
        return;
    }

    if (cave[yc, xc] != null && cave[yc, xc - 1] != null && cave[yc, xc + 1] != null)
    {
        DrawAndReset(yc - 1, xc, cave, isPart2);
        if (yc - 1 == 0 && xc == 250)
            canAddSand = false;
        return;
    }

    //Left
    if (cave[yc, xc - 1] == null)
    {
        xS--;
        return;
    }

    //Right
    if (cave[yc, xc + 1] == null)
    {
        xS++;
        return;
    }
}